import{_ as a,c as i,o as l,V as e}from"./appads.jk3shxlx.js";const m=JSON.parse('{"title":"2024 - ベトナム","description":"","frontmatter":{},"headers":[],"relativePath":"trip/2024-vietnum.md","filePath":"trip/2024-vietnum.md","lastUpdated":1721509489000}'),t={name:"trip/2024-vietnum.md"},r=e('<h1 id="_2024-ベトナム" tabindex="-1">2024 - ベトナム <a class="header-anchor" href="#_2024-ベトナム" aria-label="Permalink to &quot;2024 - ベトナム&quot;">​</a></h1><h2 id="旅行情報" tabindex="-1">旅行情報 <a class="header-anchor" href="#旅行情報" aria-label="Permalink to &quot;旅行情報&quot;">​</a></h2><ul><li>日程: 2024年1月19日〜22日</li><li>交通手段: 飛行機（Vietjet Air）</li><li>宿泊先: <ul><li>6月16・17日: <a href="https://www.booking.com/hotel/vn/the-classic-street.ja.html" target="_blank" rel="noreferrer">クラシック ストリート ホテル</a><ul><li>ベトナム旅行中で最悪のホテル体験 <ul><li>14時半頃チェックイン、友人とランチの約束があると伝えているにもかかわらず英語の練習に付き合わされチェックインに30分以上かかる（観光案内や自分の話ばかりでWi-Fiや朝食の案内は無し）</li><li>エレベーター無しの6階</li><li>シティビューと記載の部屋を予約したが周りが建物に囲まれていて全く見えず</li><li>チェックイン直後に1人がけのソファの横にゴキブリの死体有り</li><li>6時45分の朝食開始時刻は過ぎているにもかかわらず、スタッフはダラダラと朝食準備をしている、すでに並べてある小さめの肉まんは蒸されておらず未調理、座ってから30分ほど経過しやっとバイキングに含まれていない単品メニューを取りに来る（その時にはすでに食べ終わっており提供を断った）</li><li>チェックアウト時も「急いでいる」と伝えているにもかかわらず、滞在中どうだったかなどのアンケート記入やレビューを強要された</li><li>上記の旨をBooking.comのレビューに掲載すると1,500円ぽっちの返金を提示された、受け取るとレビューを消せなどとうるさそうなので無視している</li></ul></li></ul></li><li>6月18日: 執筆中</li><li>6月19日: 執筆中</li></ul></li></ul><h2 id="訪問先" tabindex="-1">訪問先 <a class="header-anchor" href="#訪問先" aria-label="Permalink to &quot;訪問先&quot;">​</a></h2><h3 id="_6月16日" tabindex="-1">6月16日 <a class="header-anchor" href="#_6月16日" aria-label="Permalink to &quot;6月16日&quot;">​</a></h3><ul><li>成田空港8時台発の飛行機でノイバイ国際空港へ</li><li>Booking.com経由の空港→ホテルの送迎タクシーを利用してホテルへ。ホテルを予約した名前の書いたプラカードを持ったおじさんが空港を出たところで待機していた。そのおじさんに話しかけるもちょっと待っててと言われ、暑い中20分程度待たされた。運転手は親切だったが結局Grabで車を捕まえたほうが早かった気がする。</li><li>ホテルに着いてからチェックインの手続き、詳細は上述。</li><li><a href="https://maps.app.goo.gl/k537phyiyZerYCiU6" target="_blank" rel="noreferrer">Bun Cha Ta Hanoi</a>でランチを食べていた日本からの友人たちに合流、ブンチャを食べた。</li><li><a href="https://maps.app.goo.gl/PvWFXgLYGm7fdwoU9" target="_blank" rel="noreferrer">Cafe Giảng</a>でベトナムエッグコーヒーを飲んだ。個人的にエッグコーヒー自体があまり好みではない。</li><li>壁画ストリート、とにかく暑かったのでインスタ映えどころではない。</li><li><a href="https://maps.app.goo.gl/ukRTgk7sRPySt5YY9" target="_blank" rel="noreferrer">Banh Mi Hoi An</a>でバインミーを購入、ベトナムのバインミーは安くて美味しい。</li><li>この先執筆中</li></ul>',6),o=[r];function n(s,h,_,c,p,u){return l(),i("div",null,o)}const f=a(t,[["render",n]]);export{m as __pageData,f as default};
